<template>
  <div class="metaWrapper">
    <img 
      src="data:image/jpeg;base64," 
      class="metaImage">
    <span class="metaName"> {{name}} </span>
  </div>
</template>

<script>
export default {
  name: "MetaCard",
  data() {
    return {
      name: "Kotaro Daigo",
    }
  }  
}
</script>
//1

<style>
  .metaWrapper {
    width: 20%;
    height: 120px;
    border: dashed black;
    margin-left: 50px;
    margin-top: 20px;
    margin-bottom: 10px;
    display: grid;
    grid-template: 10px 45px 20px 45px 10px / 10px 80px 1fr;
  }
  .metaImage {
    height: 90px;
    margin: 5px;
    grid-area: 2 / 2 / 5 / 3;
  }
  .metaName {
    color: black;
    font-size: 20px;
    grid-area: 3 / 3 / 4 / 4;
    margin-right: 5px;
    margin-left: 5px; 
  }
</style>
